INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('1', 'UP (2009)', 60000, 'Pete Docter, Blob Peterson', 'Ed Asner, Jordan Nagai, Christopher Plummer', 90, 'Phim tập trung vào Carl Fredricksen, một người góa phụ già đi đến Nam Mỹ cùng với Russell, một hướng đạo sinh, để thực hiện lời hứa mà ông đã hứa với vợ quá cố của mình, Ellie. Trên đường đi, họ gặp một con chó biết nói tên là Dug và gặp một con chim khổng lồ tên là Kevin, người đang bị thợ săn Charles Muntz - người mà Carl từng ngưỡng mộ từ thời thơ ấu - săn đuổi', 'https://www.youtube.com/embed/pkqzFUhGPJg?si=ojO_TjUW3ZWnLmG0', '/resources/images/upload/f5d39307-328d-4da1-b417-743c1994817f.webp');
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('2', 'Sing', 60000, 'Garth Jennings, Christophe Lourdelet', 'Matthew McConaughey, Reese Witherspoon, Seth MacFarlane, ...', 80, 'Phim kể về một chú gấu trúc tên Buster Moon, người quản lý một nhà hát đã xuống cấp. Ông quyết định tổ chức một cuộc thi âm nhạc để cứu nhà hát khỏi phá sản. Các thí sinh tham gia cuộc thi là những con vật có đủ sự đa dạng: từ chú khỉ đam mê rock, chú voi có giọng ca ngọt ngào, đến cô gái chuột có ước mơ trở thành ngôi sao pop.', 'https://www.youtube.com/embed/9qPgK_u4vX8?si=eHjqINHvupyNfMNP', '/resources/images/upload/a5ffde0c-147b-4bf5-b5c7-1fd14202560e.webp');
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('6', 'Cô Ba Sài Gòn', 59000, 'Trần Bửu Lộc', 'NSND Hồng Vân, Diễm My, Ngô Thanh Vân, ...', 86, e'Mô tả: Phim lấy bối cảnh chính là Sài Gòn (hay Thành phố Hồ Chí Minh) của hai năm 1969 và 2017. Truyện phim lấy chủ đề thời trang kết hợp yếu tố xuyên không kỳ ảo. Chủ đề chính của phim là tôn vinh áo dài, trang phục truyền thống của Việt Nam. 
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('7', 'Nhắm Mắt Thấy Mùa Hè', 75000, 'Cao Thuý Nhi', 'Phương Anh Đào, Takafumi Akutsu', 89, e'Mô tả: Phim kể về hành trình tâm lý của Hạ và Akira, nói về tình yêu của hai bạn trẻ người Việt Nam và người Nhật Bản.
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('3', 'Black Panther (2018)', 68000, 'Ryan Coogler', 'T''Challa', 90, e'Phim kể về T’Challa, người kế nhiệm ngai vàng Wakanda sau cái chết của cha mình. T’Challa trở về quê hương Wakanda, một quốc gia châu Phi tiên tiến và cô lập, để trở thành vị vua mới. Tuy nhiên, T’Challa sớm phát hiện ra rằng anh đang bị thách thức cho ngôi vương từ các phe phái trong chính quốc gia của mình. 
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('8', 'Em Chưa 18', 72000, 'Lê Thanh Sơn', 'Kiều Minh Tuấn, Kaity Nguyễn, Will, Quang Minh, Huy Khánh', 102, e'Phim kể về chuyện tình giữa Hoàng, một huấn luyện viên Yoga và cũng là một tay chơi sát gái, và Linh Đan, một cô gái trẻ chưa đủ 18 tuổi. 
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('5', 'Mắt Biếc', 65000, 'Victor Vũ', 'Trần Nghĩa, Trúc Anh, Khánh Vân', 89, 'Phim xoay quanh mối tình đơn phương của Ngạn với Hà Lan, cô bạn gái có cặp mắt hút hồn nhưng cá tính bướng bỉnh. Một chuyện tình nhiều cung bậc, từ ngộ nghĩnh trẻ con, rồi tình yêu thuở học trò trong sáng, trải qua bao biến cố, trở thành một cuộc “đuổi hình bắt bóng” buồn da diết nhưng không nguôi hi vọng', 'https://www.youtube.com/embed/KSFS0OfIK2c?si=PvkKosAyW5vX4aMu', '/resources/images/upload/72fee823-3a2b-440a-94d3-6604620c07f7.webp');
INSERT INTO public.film (film_id, film_name, film_price, film_director, film_cast, film_length, film_description, film_trailer_link, img_path) VALUES ('4', 'Avengers: Endgame', 70000, 'Anthony Russo, Joe Russo', 'Chris Evans, Robert Downey Jr., Mark Ruffalo, ...', 105, e'“Avengers: Hồi kết” (tựa gốc tiếng Anh: Avengers: Endgame) là phim điện ảnh siêu anh hùng Mỹ ra mắt năm 2019, do Marvel Studios sản xuất và Walt Disney Studios Motion Pictures phân phối độc quyền tại thị trường Bắc Mỹ.
